mapper.select:
select
*
from mall_product_media ct

	 and ct.uuid = #{uuid}   
	 and ct.name like CONCAT('%',#{name},'%')    
	 and ct.mediaId = #{mediaId}   
	 and ct.productId = #{productId}   

order by ct.createDate asc

mapper.delete:
delete from mall_product_media

	 and  uuid = #{uuid} 
	 and mediaId = #{mediaId}   
	 and productId = #{productId}
